      '/nlds/', // neulion live
      '/nldsu/',
      '/nlds_vod/', // neulion vod
      '/nldsu_vod/'
    ],
    /**
     * streaming parameters for DASH
     */
    streaming: {
      neulionGapDealing: true,
      safeEdgeEndSize: 0.10,
      safeEdgeBeginSize: 0.02,
      ignoreTextStreamFailures: true,
      jumpLargeGaps: true
    },
    /**
     * Preferred audio lauguage, 2*3ALPHA, default not set
     * @see https://tools.ietf.org/html/rfc5646
     */
    preferredAudioLanguage: '',
    /**
     * Preferred text lauguage, 2*3ALPHA, default not set
     * @see https://tools.ietf.org/html/rfc5646
     */
