    /**
     * Setttings of closed captions, including display name and language(2*3ALPHA)
     */  
    closedCaption: {
      label: 'English(CC)',
      language: 'en'
    },
    /**
     * Gap tolerance of  DASH
     */      
    debugConfig: {
      gapTolerance: {
        firefox: 0.02,
        ie: 0.02,
        edge: 0.02,
        chrome: 0.03,
        safari: 0.1,
        others: 0.02
      }
    }
  },
  /**
   * Start offset to trigger playback
   */     
  SMALL_GAP_TO_START: 0.01,
  DEFAULT_LOG_LEVEL: LogUtil.LogLevel.DEBUG,
  /**
   * Disable right click on video element by default
   */
  DISABLE_CONTEXT_MENU: true,
  /**
   * parameters of debug panel
   */  
  DEBUG_PANEL: {
    REFRESH_INTERVAL: 1000, // every 1 seconds
    ACTIVATE_AREA: 1 / 8, // left-up 1/5
    DATA_BUFFER_SIZE: 300 * 1000, // 5 minutes
    HOTKEYS: { ctrl: false, alt: true, shift: false } // alt + double click
  },
  /**
   * parameters of webrtc player
   */  
  WEBRTCPLAYER: {
    DEFAULT_ICE_SERVERS: [
      {
        urls: [
          'stun:stun.l.google.com:19302',
          'stun:stun1.l.google.com:19302'
        ]
      }
    ],
    WS_NORMAL_CLOSE: 3001
  },
  /**
   * parameters of basic player
   */ 
