   * parameters of DASH player
   */ 
  DASHPLAYER: {
    DEFAULT_SUBTITLE_TRACK_NAME: 'Shaka Player TextTrack',
    SEGMENT_URI_ARRAY_LENGTH: 20
  },
  /**
   * for debug purpose, enable log per components
   * show all logs of component set true
   */
  LOG_HIGHLIGHTED: {
    // managers
    // 'MultiViewsManager': true,
    // 'DebugPanel': false,
    // 'StateManager': false,
    // 'StatisticManager': false,
    // 'CuepointManager': false,
    // players
    // 'DashPlayer': false,
    // 'HlsPlayer': false,
