/**
 * class EventManager
 */
export class EventManager {
  constructor() {
    /**
     * Maps an event type to an array of event bindings.
     * @private {shaka.util.MultiMap.<!shaka.util.EventManager.Binding_>}
     */
    this.bindingMap_ = new MultiMap();
  }

  /**
   * Detaches all event listeners.
   * @override
   */
  destroy() {
    this.removeAll();
    this.bindingMap_ = null;
    return Promise.resolve();
  }

  /**
   * Attaches an event listener to an event target.
   * @param {EventTarget} target The event target.
   * @param {string} type The event type.
   * @param {shaka.util.EventManager.ListenerType} listener The event listener.
   */
  listen(target, type, listener) {
    let binding = new Binding_(target, type, listener);
    try {
      this.bindingMap_.push(type, binding);
    } catch (e) {
      // console.info(e);//
    }
  }

  /**
   * Detaches an event listener from an event target.
   * @param {EventTarget} target The event target.
   * @param {string} type The event type.
   */
  unlisten(target, type) {
    if (this.bindingMap_ === null || this.bindingMap_.length === 0) {
      return;
    }
    let list = this.bindingMap_.get(type) || [];

    for (let i = 0; i < list.length; ++i) {
      let binding = list[i];

      if (binding.target === target) {
        binding.unlisten();
        this.bindingMap_.remove(type, binding);
      }
    }
  }

  /**
   * Detaches all event listeners from all targets.
   */
  removeAll() {
    try {
      let list = this.bindingMap_.getAll();

      for (let i = 0; i < list.length; ++i) {
        list[i].unlisten();
      }

      this.bindingMap_.clear();
    } catch (error) {
      // console.log('EventManager.prototype.removeAll: error detected')
    }
  }
}

class Binding_ {
  constructor(target, type, listener) {
    /** @type {EventTarget} */
    this.target = target;

    /** @type {string} */
    this.type = type;

    /** @type {?shaka.util.EventManager.ListenerType} */
    this.listener = listener;

    try {
      this.target.addEventListener(type, listener, false);
    } catch (e) {
      // console.info(e);//
    }
  }

  /**
   * Detaches the event listener from the event target. This does nothing if the
   * event listener is already detached.
   */
  unlisten() {
    if (!this.target) {
      return;
    }

    this.target.removeEventListener(this.type, this.listener, false);

    this.target = null;
    this.listener = null;
  }
}

/**
 * Event Type
 * @readonly
 * @enum {string}
 * @static
 * @type EventManager.EventType
 */
EventManager.EventType = {
  PlayStart: 'playStart',
  /** From 3.0.0, triggered both when completed or aborted, need to check event.details.reason */
  PlayEnd: 'playEnd',
  Error: 'error',
  StateChange: 'stateChange',
  ProfileChange: 'profileChange',
  VolumeChange: 'volumeChange',
  TrackChange: 'trackChange',
  TimeUpdate: 'timeUpdate',
  Progress: 'progress',
  RateChange: 'rateChange',
  /** Trigger only once for CVOD, periodicly for live stream */
  SeekRangeChange: 'seekRangeChange',
  /** events from streaming core like Shaka and hls.js */
  StreamingEvent: 'streaming',
  /** callback with ID3Message  */
  ID3Event: 'id3',
  /** since v2.2.2 */
  AdEvent: 'advertisement',
  /** since v3.0.0 */
  AutoPlayStatus: 'autoplaystatus'
};

import { AdaptivePlayer } from '../AdaptivePlayer';
import { Logger } from '../Utils/Logger';

export class MultiviewsManager extends Logger {
  constructor(player) {
    /**
     * player should have following interface:
     * currentTime, play(), pause(), stop(), getState. event:StateChange,
     */
    super(player._logger);

    this._player = player;
    this._syncupPlayers = null;
    this._syncTimer = null;
    this._resumeTimer = null;

    this.MIN_TIME_OFFSET = 0.2; // in seconds
    this.SEEK_RANGE = 3; // in seconds
    this.SYNC_TIMER_INTERVAL = 200; // in milliseconds
  }

  _onStateChanged(evt) {
    this._syncUp(evt.target);
  }

  _getTimeOffset() {
    if (!this._syncupPlayers) {
      return 0;
    }
    if (!this._player.src === '') {
      return 0;
    }

    let minTime = this._player.currentTime;
    let maxTime = minTime;

    for (let i = 0; i < this._syncupPlayers.length; i++) {
      let syncPlayer = this._syncupPlayers[i];
      if (!syncPlayer.src || syncPlayer.readyState < 2) {
        continue;
      }
      let time = syncPlayer.currentTime;
      if (time < minTime) {
        minTime = time;
      }
      if (time > maxTime) {
        maxTime = time;
      }
    }
    this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'sync timeOffset: ' + Math.abs(maxTime - minTime));
    return Math.abs(maxTime - minTime);
  }

  _onCheckTimer() {
    if (!this._syncupPlayers && this._syncTimer) {
      this._syncTimer = window.clearInterval(this._syncTimer);
    }
    this._syncUp(this._player);
  }

  setSyncViews(array) {
    if (this._syncupPlayers) {
      this._syncupPlayers.forEach(function (player) {
        this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'remove secondary player: ' + player.viewId);
        player.removeEventListener(AdaptivePlayer.EventType.StateChange, this._onStateChanged.bind(this));
      }.bind(this));
      this._player.removeEventListener(AdaptivePlayer.EventType.StateChange, this._onStateChanged.bind(this));
      clearInterval(this._syncTimer);
    }

    if (array.length > 0) {
      this._syncupPlayers = [];
      let viewId = 0;
      array.forEach(function (player) {
        if (player) {
          player.viewId = 'player' + viewId++;
          this._syncupPlayers.push(player);
          // add event
          player.addEventListener(AdaptivePlayer.EventType.StateChange, this._onStateChanged.bind(this));
          this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'secondary player: ' + player.viewId + ' added!');
        }
      }.bind(this));
      // reset event listener
      this._player.addEventListener(AdaptivePlayer.EventType.StateChange, this._onStateChanged.bind(this));

      this._syncTimer = window.setInterval(this._onCheckTimer.bind(this), 'MultiviewManager'.SYNC_TIMER_INTERVAL);
    }
  }

  _syncUp() {
    if (!this._syncupPlayers) {
      return;
    }
    let syncPlayer = this._syncupPlayers.filter(function (item) {
      return !!item.src && item.readyState > 2;
    });
    if (syncPlayer.length <= 0) {
      this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'secondary Player not ready');
      return;
    }

    // Check all the speed
    let playbackRate = this._player.playbackRate;
    this._syncupPlayers.forEach(function (syncPlayer) {
      if (Math.abs(syncPlayer.playbackRate - playbackRate) > 0.1) {
        this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'sync secondary playbackRate: ' + syncPlayer.viewId + ' : ' + syncPlayer.playbackRate + '->' + playbackRate);
        syncPlayer.playbackRate = playbackRate;
      }
    }.bind(this));

    // Check and make sure all player are playing
    let playerState = this._player.getState();
    if (playerState === AdaptivePlayer.State.BUFFERING || playerState === AdaptivePlayer.State.PLAYING || playerState === AdaptivePlayer.State.PAUSED || playerState === AdaptivePlayer.State.SEEKING) {
      this._syncupPlayers.forEach(function (syncPlayer) {
        if (syncPlayer.getState() === AdaptivePlayer.State.IDLE && syncPlayer.src) {
          this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'load secondary Player: ' + syncPlayer.viewId);
          syncPlayer.load();
        }
      }.bind(this));

      if (this._player._playerType === AdaptivePlayer.PlayerType.NATIVE) {
        this._syncNativePlay();
      } else {
        this._syncPlay();
      }
    }

    // Check and make sure the videoDisplay is stopped
    if (this._player.getState() === AdaptivePlayer.State.IDLE) {
      this._syncupPlayers.forEach(function (syncPlayer) {
        if (syncPlayer.getState() !== AdaptivePlayer.State.IDLE) {
          this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'stop secondary Player: ' + syncPlayer.viewId);
          syncPlayer.stop();
        }
      }.bind(this));
    }
  }

  _innerSeek(player, targetTime, seekEnd) {
    if (!player || !player.src) {
      return;
    }
    let onSeeking = function () {
      player.removeEventListener('seeking', onSeeking);
      player.addEventListener('seeked', onSeeked);
    };
    let onSeeked = function () {
      player.removeEventListener('seeked', onSeeked);
      player.addEventListener('timeupdate', onTimeUpdate);
      window.setTimeout(function () {
        player.removeEventListener('timeupdate', onTimeUpdate);
        seekEnd(player.currentTime);
      }, 500);
    };
    let onTimeUpdate = function () {
      player.removeEventListener('timeupdate', onTimeUpdate);
      seekEnd(player.currentTime);
    };

    player.addEventListener('seeking', onSeeking);
    player.currentTime = targetTime;
  }

  _syncPlay() {
    let playheadTime = this._player.currentTime;
    this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'Main Player CurrentTime: ' + playheadTime);
    this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'Main Player State: ' + this._player.getState());

    let syncPlayer = this._syncupPlayers.filter(function (item) {
      return !!item.src && item.readyState > 2;
    });
    if (syncPlayer.length <= 0) {
      this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'secondary Player not ready');
      return;
    }

    if (this._player.getState() === AdaptivePlayer.State.PAUSED) {
      this._syncupPlayers.forEach(function (syncPlayer) {
        if (syncPlayer.getState() === AdaptivePlayer.State.PLAYING || syncPlayer.getState() === AdaptivePlayer.State.BUFFERING) {
          this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'made secondary Player Paused : ' + syncPlayer.viewId);
          syncPlayer.pause();
        }
      }.bind(this));

      this._syncupPlayers.forEach(function (syncPlayer) {
        if (syncPlayer.getState() === AdaptivePlayer.State.PAUSED) {
          let deltaTime = playheadTime - syncPlayer.currentTime;
          if (Math.abs(deltaTime) > this.MIN_TIME_OFFSET) {
            this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'made secondary Player seek to main Player playheadTime: ' + syncPlayer.viewId + ' : ' + syncPlayer.currentTime + '->' + playheadTime);
            syncPlayer.currentTime = playheadTime;
          }
        }
      }.bind(this));
      return;
    }

    if (this._player.getState() === AdaptivePlayer.State.PLAYING) {
      let halfOffset = this.MIN_TIME_OFFSET / 2.0;
      let playbackRate = this._player.playbackRate;
      if (playbackRate > 0) {
        halfOffset *= playbackRate;
      }
      let timeLeftOfOffset = playheadTime - halfOffset;
      let timeRightOfOffset = playheadTime + halfOffset;

      let timeSeek = playheadTime + this.SEEK_RANGE; // 4s
      this._syncupPlayers.forEach(function (syncPlayer) {
        if (syncPlayer.getState() === AdaptivePlayer.State.SEEKING || syncPlayer.getState() === AdaptivePlayer.State.IDLE) {
          return;
        }
        let time0 = syncPlayer.currentTime;
        this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'secondary Player CurrentTime: ' + syncPlayer.viewId + ' : ' + time0 + ',' + timeSeek + ', [' + timeLeftOfOffset + ', ' + timeRightOfOffset + ']');
        if (time0 < timeLeftOfOffset || time0 > timeSeek) {
          if (syncPlayer.getState() === AdaptivePlayer.State.PLAYING || syncPlayer.getState() === AdaptivePlayer.State.BUFFERING || syncPlayer.getState() === AdaptivePlayer.State.PAUSED) {
            this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'made secondary Player Paused ' + syncPlayer.viewId + ':');
            syncPlayer.pause();
            this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'made secondary Player seek to main Player playheadTime: ' + syncPlayer.viewId + ' : ' + time0 + '->' + timeSeek);
            syncPlayer.currentTime = timeSeek;
          }
        } else if (time0 > timeRightOfOffset) {
          if (syncPlayer.getState() === AdaptivePlayer.State.PLAYING) {
            this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'made secondary Player Paused' + syncPlayer.viewId + ' :');
            syncPlayer.pause();
          }
        } else if (time0 >= timeLeftOfOffset && time0 <= timeRightOfOffset) {
          if (syncPlayer.getState() === AdaptivePlayer.State.PAUSED) {
            this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'made secondary Player Play' + syncPlayer.viewId + ' :');
            syncPlayer.play();
          }
        }
      }.bind(this));

      this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'Main Player State: ' + this._player.getState());
      this._syncupPlayers.forEach(function (player) {
        if (!player.src || player.readyState < 2) {
          return;
        }
        this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'secondary Player State: ' + player.viewId + '/' + player.getState());
      }.bind(this));
    }
  }

  _syncNativePlay() {
    let syncPlayer = this._syncupPlayers.filter(function (item) {
      return !!item.src && item.readyState > 2;
    })[0];
    if (!syncPlayer) {
      this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'secondary Player not ready');
      return;
    }

    let playheadTime = this._player.currentTime;
    let halfOffset = this.MIN_TIME_OFFSET / 2.0;
    let playbackRate = this._player.playbackRate;
    if (playbackRate > 0) {
      halfOffset *= playbackRate;
    }
    let timeLeftOfOffset = playheadTime - halfOffset;
    let timeRightOfOffset = playheadTime + halfOffset;
    let timeSeek = playheadTime + this.SEEK_RANGE; // 4s
    let syncPause = false;
    let inSyncing = false;
    this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'Main Player CurrentTime: ' + playheadTime);
    this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'Main Player State: ' + this._player.getState());

    if (this._player.getState() === AdaptivePlayer.State.PAUSED) {
      if (syncPlayer.getState() === AdaptivePlayer.State.PLAYING || syncPlayer.getState() === AdaptivePlayer.State.BUFFERING || syncPlayer.getState() === AdaptivePlayer.State.PAUSED) {
        this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'secondary Player CurrentTime: ' + syncPlayer.currentTime);
        let deltaTime = playheadTime - syncPlayer.currentTime;
        if (Math.abs(deltaTime) > this.MIN_TIME_OFFSET) {
          if (inSyncing) {
            return;
          }
          this._innerSeek(syncPlayer, timeSeek, function (timeSeekResult) {
            this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'inner seek: ' + 'currentTime: ' + syncPlayer.currentTime + ' to: ' + timeSeek + ' accurate: ' + timeSeekResult);
            if (timeSeekResult >= timeLeftOfOffset) {
              this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'set secondary Player inSyncing true, made secondary player pause');
              syncPlayer.pause();
            }
          }.bind(this));
          inSyncing = true;
        } else if (syncPause) {
          syncPlayer.play();
          this._player.play();
          inSyncing = false;
          syncPause = false;
          this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'set secondary Player inSyncing false, made MainPlayer & SecondarayPlayer play');
        }
      }
      return;
    }

    if (this._player.getState() === AdaptivePlayer.State.PLAYING) {
      if (syncPlayer.getState() === AdaptivePlayer.State.SEEKING || syncPlayer.getState() === AdaptivePlayer.State.IDLE) {
        return;
      }
      let time0 = syncPlayer.currentTime;
      this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'secondary Player CurrentTime: ' + syncPlayer.viewId + ' : ' + time0);
      if (time0 < timeLeftOfOffset || time0 > timeSeek) {
        if (syncPlayer.getState() === AdaptivePlayer.State.PLAYING || syncPlayer.getState() === AdaptivePlayer.State.BUFFERING || syncPlayer.getState() === AdaptivePlayer.State.PAUSED) {
          this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'secondary Player currentTime: ' + time0);
          if (inSyncing) {
            return;
          }
          this._innerSeek(syncPlayer, timeSeek, function (timeSeekResult) {
            this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'inner seek: ' + 'currentTime: ' + syncPlayer.currentTime + ' to: ' + timeSeek + ' accurate: ' + timeSeekResult);
            if (timeSeekResult > timeRightOfOffset) {
              this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'set secondary Player inSyncing true, made secondary player pause');
              syncPlayer.pause();
            } else if (timeSeekResult < timeLeftOfOffset) {
              this._player.pause();
              syncPause = true;
              this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'set MainPlayer inSyncing true, made MainPlayer pause');
            } else {
              inSyncing = false;
              this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'set MainPlayer inSyncing false, seek point is in sync range');
            }
          }.bind(this));
          inSyncing = true;
        }
      } else if (time0 >= timeLeftOfOffset && time0 <= timeRightOfOffset) {
        if (syncPlayer.getState() === AdaptivePlayer.State.PAUSED) {
          this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'set secondary Player inSyncing false, made secondary player play');
          syncPlayer.play();
        }
        if (inSyncing) {
          inSyncing = false;
        }
      }

      return;
    }
    this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'Main Player State: ' + this._player.getState());
    this.print(AdaptivePlayer.LogLevel.TRACE, 'MultiviewManager', 'secondary Player State: ' + syncPlayer.getState());
  }
}

import { EventManager } from './EventManager';
import { Logger } from '../Utils/Logger'
import { AdaptivePlayer } from '../AdaptivePlayer';

/**
 * class StateManager
 */
export class StateManager extends Logger {
  constructor(parent, stateChangeCallback) {
    super(parent._logger);

    this._video = parent._video;
    this._state = StateManager.State.IDLE;
    this._playStarted = false;
    this._inUserSeeking = false;
    this._inSeeking = false;
    this._bufferingTime = 0.0;
    this._startBufferingTime = -1;
    this._stateCallback = stateChangeCallback;

    this._eventManager = new EventManager();
  }

  destroy() {
    // this._clearTimer();
    this._video = null;
    this._player = null;
    this._eventManager && this._eventManager.removeAll();
  }

  _onLoadStart(event) {
    // skip MEDIA_ELEMENT_ERROR: Empty src attribute
    if (this._video.error && this._video.error.code === 4) {
      return; 
    }
    this._setState(StateManager.State.BUFFERING, { 'from': 'loadstart' });
  }

  // TODO:: Need to suppress the update.
  _onTimeUpdate(event) {
    if (this._video.playbackRate === 0 || this._inSeeking) {
      return;
    }
    if (this._video.paused) {
      this._setState(StateManager.State.PAUSED, { 'from': 'timeupdate' });
    } else {
      this._setState(StateManager.State.PLAYING, { 'from': 'timeupdate' });
    }
  }

  _onCanPlayThrough(event) {
    if (this._video.paused) {
      this._setState(StateManager.State.PAUSED, { 'from': 'canplaythrough' });
    } else {
      this._setState(StateManager.State.PLAYING, { 'from': 'canplaythrough' });
    }
  }
  _onPlaying(event) {
    // this._setState(StateManager.State.BUFFERING, {'from': 'playing'});
  }
  _onPlay(event) {
    this._setState(StateManager.State.STARTPLAY, { 'from': 'play' });
  }
  _onPause(event) {
    this._setState(StateManager.State.PAUSED, { 'from': 'paused' });
  }
  _onEnded(event) {
    this.reset({ reason: StateManager.ActionReason.COMPLETE });
  }
  _onAbort(event) {
    // this.reset({ reason: StateManager.ActionReason.ABORT });
  }
  _onError(event) {
    this.reset({ reason: StateManager.ActionReason.ERROR });
  }
  _onWaiting(event) {
    this._setState(StateManager.State.BUFFERING, { 'from': 'waiting' });
  }
  _onStalled(event) {
    this._setState(StateManager.State.BUFFERING, { 'from': 'stalled' });
  }
  _onRateChange(event) {
    if (this._video.playbackRate === 0 && this._playStarted) {
      this._setState(StateManager.State.BUFFERING, { 'from': 'ratechange' });
    }
  }
  _onSeeking(event) {
    this._inSeeking = true;
    this._setState(StateManager.State.SEEKING, { 'from': 'seeking' });
  }
  _onSeeked(event) {
    this._inSeeking = false;
    this._inUserSeeking = false;
    this._setState(StateManager.State.BUFFERING, { 'from': 'seeked' });
  }

  // TODO:: Rename to reset
  reset(details) {
    this._setState(StateManager.State.IDLE, { from: details });

    this._playStarted = false;
    this._inUserSeeking = false;
    this._startBufferingTime = -1;
    this._bufferingTime = 0.0;

    if (details.reason === StateManager.ActionReason.START) {
      this._eventManager.listen(this._video, 'loadstart', this._onLoadStart.bind(this));
      this._eventManager.listen(this._video, 'ended', this._onEnded.bind(this));
      this._eventManager.listen(this._video, 'abort', this._onAbort.bind(this));
      // this._eventManager.listen(this._video, 'error', this._onError.bind(this));

      this._eventManager.listen(this._video, 'seeking', this._onSeeking.bind(this));
      this._eventManager.listen(this._video, 'seeked', this._onSeeked.bind(this));

      this._eventManager.listen(this._video, 'timeupdate', this._onTimeUpdate.bind(this));

      this._eventManager.listen(this._video, 'waiting', this._onWaiting.bind(this));
      // this._eventManager.listen(this._video, 'stalled', this._onStalled.bind(this));
      this._eventManager.listen(this._video, 'ratechange', this._onRateChange.bind(this));

      this._eventManager.listen(this._video, 'canplaythrough', this._onCanPlayThrough.bind(this));
      // this._eventManager.listen(this._video, 'playing', this._onPlaying.bind(this));

      this._eventManager.listen(this._video, 'play', this._onPlay.bind(this));
      this._eventManager.listen(this._video, 'pause', this._onPause.bind(this));
    } else if (details.reason === StateManager.ActionReason.ABORT || details.reason === StateManager.ActionReason.ERROR) {
      this._eventManager.removeAll();
    }
  }

  notifyUserSeek() {
    this._inUserSeeking = true;
  }

  _setState(newState, details) {
    if (newState === this._state) {
      return;
    }

    let oldState = this._state;
    // if (oldState == StateManager.State.SEEKING && newState == StateManager.State.BUFFERING){
    //     return;
    // }
    this._state = newState;

    if (newState === StateManager.State.PLAYING) {
      this._playStarted = true;
      details = details || {};
      details.playStart = true;
      if (this._startBufferingTime > 0) {
        this._bufferingTime += Date.now() - this._startBufferingTime;
        this.print(AdaptivePlayer.LogLevel.DEBUG, 'StateManager', 'End Trigger BufferTime: ' + this._bufferingTime + ' from: ' + details.from);
        this._startBufferingTime = -1;
      }
    } else if (newState === StateManager.State.BUFFERING || newState === StateManager.State.SEEKING) {
      if (this._startBufferingTime < 0) {
        this._startBufferingTime = Date.now();
        this.print(AdaptivePlayer.LogLevel.DEBUG, 'StateManager', 'Start Trigger BufferTime: ' + this._startBufferingTime + ' from: ' + details.from);
      }
    }
    this._stateCallback(oldState, newState, details);
    this.print(AdaptivePlayer.LogLevel.DEBUG, 'StateManager', 'Fired StateChange: ' + oldState + '->' + newState);
  }

  getState() {
    return this._state;
  }
  getBufferingTime() {
    return this._bufferingTime;
  }
}

/**
 * State
 * @readonly
 * @enum {string}
 * @static
 * @type StateManager.State
 */
StateManager.State = {
  IDLE: 'idle',
  LOADING: 'loading',
  BUFFERING: 'buffering',
  STARTPLAY: 'play',
  PLAYING: 'playing',
  PAUSED: 'paused',
  SEEKING: 'seeking'
};

/**
 * Reason of playback end
 * @readonly
 * @enum {string}
 */
StateManager.ActionReason = {
  START: 'start',
  ABORT: 'abort',
  COMPLETE: 'complete',
  ERROR: 'error'
}

import { AdaptivePlayer } from '../AdaptivePlayer';
import { NLError } from '../Media/NLError'
import { FakeEvent } from '../Utils/FakeEvent';
import { Logger } from '../Utils/Logger'

export class StatisticManager extends Logger {
  constructor(video, parent) {
    super(parent._logger);

    this._eventHandler = parent;
    this._video = video;
    this.startWatchDog = null;
    this.seekWatchDog = null;

    this._onError = null;
    this._errConfig = {
      url: '',
      level: 0
    };
    this.errorCheckList = [];

    StatisticManager.WATCHDOG_TIMEOUT = 10 * 1000;
    StatisticManager.ERROR_CHECK_INTERVAL = 30 * 1000;
  }

  stop() {
    this.stopLoadWatchDog();
    this.stopSeekWatchDog();
  }

  setErrorCallback(onError, errorConfig) {
    this._onError = onError;
    this._errConfig = errorConfig;
  }

  getErrorConfig() {
    return this._errConfig;
  }

  // error in [ 30s ] error don't add
  _generateCheckFunction(olderErrorInfo) {
    return function (olderErrorInfo, newerErrorInfo) {
      return arguments[0].compare(arguments[1]);
    }.bind(null, olderErrorInfo);
  }

  // return true when same mesage in 30s
  _onCheckErrorInfo(addedErrorInfo) {
    for (let i = 0; i < this.errorCheckList.length; i++) {
      let errorItem = this.errorCheckList[i];
      if (Date.now() - errorItem.time > StatisticManager.ERROR_CHECK_INTERVAL) {
        this.errorCheckList.splice(i, 1);
        i--;
        continue;
      }
      if (errorItem.checkFunc(addedErrorInfo)) {
        return true;
      }
    }

    this.errorCheckList.push({
      checkFunc: this._generateCheckFunction(addedErrorInfo),
      time: Date.now()
    });
    return false;
  }

  reset(url) {
    if (this.startWatchDog) {
      this.stopLoadWatchDog();
      this.startWatchDog = null;
    }

    if (this.seekWatchDog) {
      this.stopSeekWatchDog();
      this.seekWatchDog = null;
    }

    this.errorCheckList = [];
    this._errConfig.url = url;
    this.startLoadWatchDog();
  }

  startLoadWatchDog() {
    if (this.startWatchDog) {
      this.stopLoadWatchDog();
    }
    this.startWatchDog = window.setTimeout(function () {
      let message = 'Start playback timeout';
      let code = NLError.Code.Start_Playback_Timeout;
      let level = NLError.SeverityLevel.Warning;
      let error = NLError.create(code, level, this._errConfig.url, message);
      this.reportError(error);
    }.bind(this), StatisticManager.WATCHDOG_TIMEOUT);
  }

  startSeekWatchDog() {
    if (this.seekWatchDog) {
      this.stopSeekWatchDog();
    }
    this.seekWatchDog = window.setTimeout(function () {
      let message = 'Seek timeout';
      let code = NLError.Code.Seek_Timeout;
      let level = NLError.SeverityLevel.Warning;
      let error = NLError.create(code, level, this._errConfig.url, message);
      this.reportError(error);
    }.bind(this), StatisticManager.WATCHDOG_TIMEOUT);
  }

  stopLoadWatchDog() {
    if (this.startWatchDog) {
      this.startWatchDog = window.clearTimeout(this.startWatchDog);
    }
  }

  stopSeekWatchDog() {
    if (this.seekWatchDog) {
      this.seekWatchDog = window.clearTimeout(this.seekWatchDog);
    }
  }

  reportUnsupportedSchema(url) {
    let message = 'load with wrong url!';
    let code = NLError.Code.Get_Primary_Manifest_Failed + NLError.NetworkDetailCode.Connect_Failed;
    let level = NLError.SeverityLevel.Fatal;
    this.reportError(NLError.create(code, level, url, message));
  }

  reportUnsupported() {
    let message = 'Your Browser does not support MSE or WebRTC';
    let code = NLError.Code.Platform_Not_Supported;
    let level = NLError.SeverityLevel.Fatal;
    let error = NLError.create(code, level, this._errConfig.url, message);
    this.reportError(error);
  }

  reportNativeVideoError(msg, errorCode) {
    let code = errorCode || NLError.Code.Playback_Generic_Error;
    let level = NLError.SeverityLevel.Fatal;
    let error = NLError.create(code, level, this._errConfig.url, msg);
    this.reportError(error);
  }

  reportError(error) {
    if (!error.isValid() ||
      this._onCheckErrorInfo(error) ||
      (this._errConfig && this._errConfig.level && this._errConfig.level < error.level)) {
      return;
    }

    if (this._onError && error.level <= NLError.SeverityLevel.Error) {
      this._onError(error.toJSON());
    }

    if (error.level > NLError.SeverityLevel.Error) {
      error.code += 5000;
    }

    this._eventHandler.dispatchEvent(new FakeEvent(AdaptivePlayer.EventType.Error, { details: error.toJSON() }));
    this.print(AdaptivePlayer.LogLevel.INFO, 'StatisticManager', 'Trigger Error event: ' + JSON.stringify(error.toJSON()));
  }
}

import * as muxjs from '../../3rd/mux.js/dist/mux'

export class CCExtractor {
  constructor(ccCallback) {
    this._ccCallback = ccCallback;
    this.init_ = null;
    this.isLive_ = false;

    /**
     * Used for DASH CVOD case, only for LIVE
     * cts of first sample in timescale 1000
     */
    this.presentationTimeOffset_ = 0;

    this.closedCaptionParser_ = null;    
  }

  destroy() {

  }

  appendInit(data, isLive, timeOffsetforCVOD) {
    this.init_ = data;
    this.isLive_ = !!isLive;
    this.presentationTimeOffset_ = (this.isLive_) ? 0 : timeOffsetforCVOD; // only for CVOD case, need to minus time offset

    this.closedCaptionParser_ = new muxjs.mp4.CaptionParser();

    let probe = muxjs.mp4.probe;
    // Caption parser for Dash
    let initBytes = new Uint8Array(data);
    this.videoTrackIds_ = probe.videoTrackIds(initBytes);
    this.timescales_ = probe.timescale(initBytes);
    this.closedCaptionParser_.init();
  }

  resetData() {
    if (this.closedCaptionParser_) {
      this.closedCaptionParser_.reset();
    }
  }

  resetInitSegment() {
    this.init_ = null;
  }

  appendSegment(data) {
    let segmentBytes = new Uint8Array(data);
    let dashParsed = this.closedCaptionParser_.parse(
      segmentBytes, this.videoTrackIds_, this.timescales_);
    if (dashParsed && dashParsed.captions && dashParsed.captions.length > 0) {
      let captions = [];
      dashParsed.captions.forEach(function(caption) {
        captions.push({ 
          start: caption.startTime - this.presentationTimeOffset_, 
          end: caption.endTime - this.presentationTimeOffset_, 
          detail: caption.screen 
        });
      }.bind(this));
      this._ccCallback(captions);
    }
    // ParsedCaptions is used by mux.js to store the captions parsed so far.
    // It should be reset every time some data is parsed, so as to store new
    // data.
    this.closedCaptionParser_.clearParsedCaptions();
  }
}

import { AdaptivePlayer } from '../AdaptivePlayer'

function fixLineBreaks(input) {
  return input.replace(/<br(?: \/)?>/gi, '\n');
}

let MakeCues = function (captions, track) {
  let cues = [];
  let row;
  let cue;
  let indenting;
  let indent;
  let text;
  let VTTCue = window.VTTCue || window.TextTrackCue;

  for (let i = 0; i < captions.length; i++) {
    let caption = captions[i];
    let startTime = caption.start;
    let endTime = caption.end;
    let captionScreen = caption.detail;

    // remove cues in case of overlap
    if (track.cues) {
			for (let i = track.cues.length; i--;) {
				let cue_ = track.cues[i];
				// if (cue_.endTime < cue.startTime) { break; }
				let overlap = Math.min(cue_.endTime, endTime) - Math.max(cue_.startTime, startTime);
				if (overlap >= 0 && (overlap / (endTime - startTime)) > 0.5) {
					return;
				}
			}
		}
    for (let r = 0; r < captionScreen.length; r++) {
      row = captionScreen[r];
      indenting = true;
      indent = 0;
      text = '';
      if (row !== '') {
        for (let c = 0; c < row.length; c++) {
          if (row[c].match(/\s/) && indenting) {
            indent++;
          } else {
            text += row[c];
            indenting = false;
          }
        }
        // Give a slight bump to the endTime if it's equal to startTime to avoid a SyntaxError in IE
        if (startTime === endTime) {
          endTime += 0.0001;
        }
        cue = new VTTCue(startTime, endTime, fixLineBreaks(text.trim()));
        if (indent >= 16) {
          indent--;
        } else {
          indent++;
        }
        // VTTCue.line get's flakey when using controls, so let's now include line 13&14
        // also, drop line 1 since it's to close to the top
        // Bug #42343: [v3.0]The position of cc is incorrect when play HLS live stream in safari H5player.
        if (navigator.userAgent.match(/Firefox\//)) {
          let isMacOS = (AdaptivePlayer.OS.name === 'Mac OS');
          let OFFSET = [
            [-1, 1], // windows and others
            [2, 1] // offset on mac os
          ];
          cue.line = (r > 7 ? r + OFFSET[isMacOS & 1][0] : r + OFFSET[isMacOS & 1][1]);
        } else {
          cue.line = (r > 7 ? r - 3 : r + 1);
        }

        cue.align = 'left';
        // Clamp the position between 0 and 100 - if out of these bounds, Firefox throws an exception and captions break
        cue.position = Math.max(0, Math.min(100, 100 * (indent / 32) + (navigator.userAgent.match(/Firefox\//) ? 50 : 0)));
        track.addCue(cue);
      }
    }
  }

  return cues;
};

export {
  MakeCues
};

import { NLError } from '../NLError'
import { ServerCertificateManager } from './ServerCertificateManager'
import { KeySessionManager } from './KeySessionManager'
import { EventManager } from '../../Manager/EventManager'

export class FairPlayDRM {
    constructor() {
        this._clear();

        this._certManager = new ServerCertificateManager();
        this._eventManager = new EventManager();
        this._sessionManager = new KeySessionManager();
    }

    configure(licenseUrl, certUrl, params) {
        this._licenseUrl = licenseUrl;
        this._certUrl = certUrl;
        this._params = params;
    }

    init(video, onError) {
        this._video = video;
        this._onError = onError;

        this._sessionManager.init(
            this._licenseUrl,
            this._params,
            onError);

        if (typeof this._video.onwebkitneedkey !== 'undefined') {
            this._eventManager.listen(
                this._video,
                'webkitneedkey',
                this._onNeedKey.bind(this));
        } else {
            this._onError({ error: NLError.Basic.DRM.NOT_SUPPORTED });
        }
    }

    reset() {
        this._clear();

        // We don't want to reset CertManager because want to keep the cert in cache.
        this._eventManager && this._eventManager.removeAll();
        this._sessionManager && this._sessionManager.reset();
    }

    destroy() {
        this._clear();

        this._eventManager && this._eventManager.destroy();
        this._certManager && this._certManager.destroy();
        this._sessionManager && this._sessionManager.destroy();
    }

    _clear() {
        this._video = null;
        this._licenseUrl = '';
        this._certUrl = '';
        this._params = null;
        this._onError = null;
    }

    _onNeedKey(event) {
        if (!this._licenseUrl || !this._certUrl) {
            this.reset();
            this._onError({ error: NLError.Basic.DRM.INVALID_PARAMS });
            return;
        }

        this._certManager.load(this._certUrl, this._params.token).then(function (certCb) {
            this._sessionManager.createSession(this._video, event.initData, certCb);
        }.bind(this), function (e) {
            /* error :( */
            this._onError({ error: NLError.Basic.DRM.CERTIFICATE_ERROR, code: e });
        }.bind(this));
    }

    _onKeyError(event) {
        this._onError({ error: NLError.Basic.DRM.KEY_ERROR });
    }
}

import * as StringUtil from '../../Utils/String'

let extractContentId = function (initData) {
    // TODO::
    let contentId = StringUtil.arrayToString(initData);

    if (contentId[0] !== 's') {
        contentId = contentId.substr(1, contentId.length);
    }

    // contentId is passed up as a URI, from which the host must be extracted:
    let link = document.createElement('a');
    link.href = contentId;
    return link.hostname;
};

let concatInitDataIdAndCertificate = function (initData, id, cert) {
    if (typeof id === 'string') {
        id = StringUtil.stringToArray(id);
    }

    // layout is [initData][4 byte: idLength][idLength byte: id][4 byte:certLength][certLength byte: cert]
    let offset = 0;
    let buffer = new ArrayBuffer(initData.byteLength + 4 + id.byteLength + 4 + cert.byteLength);
    let dataView = new DataView(buffer);

    let initDataArray = new Uint8Array(buffer, offset, initData.byteLength);
    initDataArray.set(initData);
    offset += initData.byteLength;

    dataView.setUint32(offset, id.byteLength, true);
    offset += 4;

    let idArray = new Uint16Array(buffer, offset, id.length);
    idArray.set(id);
    offset += idArray.byteLength;

    dataView.setUint32(offset, cert.byteLength, true);
    offset += 4;

    let certArray = new Uint8Array(buffer, offset, cert.byteLength);
    certArray.set(cert);

    return new Uint8Array(buffer, 0, buffer.byteLength);
};

let parseLicense = function (text) {
    /* response can be of the form: 
        '{
        \'body\': {
            \'duration\': 864000, 
            \'response\': \'\'
        }, 
        \'status\': {
            \'ok\': true
        }
        }' 
    */
    let response = JSON.parse(text);
    if (!response) {
        return { result: null, message: 'response parse failed!' };
    }
    if (!response.status.ok) {
        return { result: null, message: response.status.user_msg };
    }

    return { result: StringUtil.base64DecodeUint8Array(response.body.response), message: 'ok' };
};

export {
    extractContentId,
    concatInitDataIdAndCertificate,
    parseLicense
};

import * as helpers from './FairPlayDRMHelpers'
import { EventManager } from '../../Manager/EventManager'
import * as ObjectUtil from '../../Utils/Object'
import * as StringUtil from '../../Utils/String'
import * as xhr from '../../Utils/Xhr'
import * as PlatformUtil from '../../Utils/Platform'
import { NLError } from '../NLError'

export class KeySessionManager {
    constructor() {
        this.KEY_SYSTEM_NAME = 'com.apple.fps.1_0';
        this._licenseUrl = '';
        this._onError = null;
        this._params = null;

        this._eventManager = new EventManager();
        this._sessions = [];
    }

    init(licenseUrl, params, onError) {
        this._licenseUrl = licenseUrl;
        this._params = params;
        this._onError = onError;

        this._eventManager.removeAll();
        this._clearSession();
        this._sessions = [];
    }

    createSession(videoElement, rawInitData, certCb) {
        if (!certCb) {
            // cert load failed, just return
            return;
        }

        let contentId = helpers.extractContentId(rawInitData);
        let overrideInitData = helpers.concatInitDataIdAndCertificate(
            rawInitData,
            contentId,
            new Uint8Array(certCb));

        videoElement.webkitSetMediaKeys(new WebKitMediaKeys(this.KEY_SYSTEM_NAME));

        let keySession = videoElement.webkitKeys.createSession('video/mp4', overrideInitData);
        if (!keySession) {
            this._onError({ error: NLError.Basic.DRM.KEY_SESSION_CREATE_ERROR });
            return;
        }
        this._eventManager.listen(keySession, 'webkitkeymessage', this._onKeyMessage.bind(this));
        this._eventManager.listen(keySession, 'webkitkeyerror', this._onKeyError.bind(this));

        this._sessions.push({ keySession: keySession, contentId: contentId });
    }

    reset() {
        this.init('', null, null);
    }

    destroy() {
        this.reset();
        this._eventManager.destroy();
        this._video = null;
    }

    _onKeyMessage(event) {
        let keySession = event.target;
        let session = this._getSession(keySession);

        let spcBase64 = StringUtil.base64EncodeUint8Array(event.message);
        let licenseServer = this._licenseUrl;

        let headers = {};
        let params = '';

        if (this._params) {
            // saffron request
            // {
            //  'content_id': '53504841B2694F515BCDCCE20810656E',
            //  'device_id':'8d025a8a-d0fa-47f4-8eaa-a4627d11e045',
            //  'challenge':'AAAAAQAAAACNCi9C3uGspgtxwmW',
            //  'client':'Tribeca',
            //  'cro':'bearer eyJhbGciOiJIUzI1NiIsInR5cC' 
            // }
            params = '{"content_id":"' + session.contentId + 
                '","device_id":"' + PlatformUtil.getDeviceId() + 
                '","challenge":"' + spcBase64 + 
                '","client":"' + (this._params.provider || 'NeuLion') + 
                '","cro":"' + this._params.token + '"}';

            headers['Content-type'] = 'application/x-www-form-urlencoded';
            headers['Content-Type'] = 'application/x-www-form-urlencoded';
            headers['Accept'] = 'application/json, text/plain, */*';
        }

        let async = xhr.createXHR({
            url: licenseServer,
            method: 'POST',
            headers: headers,
            params: params
        });

        async.then(function (responseText) {
            let key = helpers.parseLicense(responseText);
            if (!key.result) {
                this._onError({
                    error: {
                        code: NLError.Basic.DRM.LICENSE_ERROR.code,
                        message: NLError.Basic.DRM.LICENSE_ERROR.message + key.message
                    }
                });
            } else {
                delete session.async;
                keySession.update(key.result);
            }
        }.bind(this)).catch(function (e) {
            delete session.async;
            this._onError({ error: NLError.Basic.DRM.LICENSE_ERROR, code: e });
        }.bind(this));

        session.async = async;
    }

    _onKeyError(event) {
        this._onError({ error: NLError.Basic.DRM.KEY_ERROR });
    }

    _getSession(s) {
        return ObjectUtil.find(this._sessions, 'keySession', s);
    }

    _clearSession() {
        if (!this._sessions || this._sessions.length <= 0) {
            return;
        }

        this._sessions.forEach(function (s) {
            if (s.async) {
                s.async.abort();
            }
            if (s.keySession) {
                s.keySession.close();
            }
        });
    }
}

import * as xhr from '../../Utils/Xhr'
import * as StringUtil from '../../Utils/String'
import * as ObjectUtil from '../../Utils/Object'
import { NLError } from '../NLError'

export class ServerCertificateManager {
    constructor() {
        this.MAX_COUNT = 2;
        // {key, value, async}
        //      |key|   is the cert url.
        //      |value| is ArrayBuffer.
        //      |async| is the Promise.
        this._cache = [];
    }

    load(url, token) {
        // Make sure 'session.async' count less than MAX_COUNT.
        if (this._pendingCount() >= this.MAX_COUNT) {
            return Promise.reject(NLError.Basic.CERTIFICATE_OVERFLOW_ERROR);
        }

        let certSession = this._hasCert(url);
        if (certSession) {
            return certSession.value ? Promise.resolve(certSession.value) : certSession.async;
        }

        let headers = {};
        headers['CustomData'] = token;
        headers['Authorization'] = token;

        let async = xhr.createXHR({
            url: url,
            headers: headers
        }).then(function (ab) {
            certSession.value = StringUtil.base64DecodeUint8Array(ab.replace(/(\r\n|\n|\r)/gm, ''));
            delete certSession.async;
            return certSession.value;
        }).catch(function (e) {
            this._removeSession(certSession);
            return Promise.reject(e);
        }.bind(this));

        certSession = { key: url, async: async };

        this._pushSession(certSession);
        return async;
    }

    destroy() {
        this._cache.forEach(function (item) {
            let async = item.async;
            async && async.abort && async.abort();
        });
    }

    _hasCert(url) {
        return ObjectUtil.find(this._cache, 'key', url);
    }

    _pendingCount() {
        let n = this._cache.length;
        let count = 0;
        for (let i = 0; i < n; i++) {
            if (this._cache[i].async) {
                count++;
            }
        }
        return count;
    }

    _pushSession(session) {
        if (this._cache.length >= this.MAX_COUNT) {
            this._cache.shift();
        }
        this._cache.push(session);
    }

    _removeSession(session) {
        let idx = this._cache.indexOf(session);
        if (idx >= 0) {
            this._cache.splice(idx, 1);
        }
    }
}

import * as UrlUtil from '../Utils/Url'
import * as XHRUtil from '../Utils/Xhr'
import { Parser } from '../../3rd/m3u8-parser/dist/m3u8-parser.min'
import { NLError } from './NLError'
import { WebVTTParser } from '../../externals/webvtt/parser'

export default class HlsPlaylistParser {
  constructor() {
    this._playHeadTimeCb = null;
    this._errorCb = null;
    this._thumbnailCb = null;
    this._thumbnailImageCb = null;
    this._adCb = null;
    this._masterUrl = null;
    this._baseUrl = null;
    this._levelUrl = null;
    this._levelRedundantUrls = [];
    this._levelIndex = -1;
    this._ccInfos = null;
  }

  destroy() {
    this._playHeadTimeCb = null;
    this._errorCb = null;
    this._thumbnailCb = null;
    this._thumbnailImageCb = null;
    this._adCb = null;
    this._masterUrl = null;
    this._baseUrl = null;
    this._levelUrl = null;
    this._levelRedundantUrls = [];
    this._levelIndex = -1;
    this._ccInfos = null;
  }

  loadPlaylist(url, userdata) {
    if (this._masterUrl == null) {
      this._masterUrl = url;
      this._baseUrl = UrlUtil.getPath(url);
    }
    let async = XHRUtil.createXHREx({
      url: url
    }, userdata).then(function (ab) {
      let parser = new Parser();
      parser.push(ab.response);
      parser.end();

      if (ab.userdata && ab.userdata.type === 'master' && parser.isMasterPlaylist()) {
        try {
          this._parseMasterPlaylist(parser);
        } catch (e) {
          this._onError(NLError.Basic.M3U8.MANIFEST_PARSING_ERROR, e);
        }
      }

      if (ab.userdata && (ab.userdata.type === 'level' || ab.userdata.type === 'master') && parser.isLevelPlaylist()) {
        if (ab.userdata.type === 'master') {
          this._levelUrl = this._masterUrl;
        }
        try {
          this._parseLevelPlaylist(parser, ab.userdata.index);
        } catch (e) {
          if (ab.userdata.type === 'level' && this._levelIndex >= 0 && this._levelIndex < this._levelRedundantUrls.length) {
            this._levelIndex += 1;
            this._levelUrl = this._levelRedundantUrls[this._levelIndex];
          } else {
            this._onError(NLError.Basic.M3U8.GET_PLAYHEADTIME_FAILED, e);
          }
        }
      }

      if (ab.userdata && ab.userdata.type === 'thumbnail' && parser.isLevelPlaylist()) {
        try {
          this._parseThumbnailPlaylist(parser, ab.userdata);
        } catch (e) {
          this._onError(NLError.Basic.M3U8.GET_THUMBNAIL_FAILED, e);
        }
      }
    }.bind(this)).catch(function (e) {
      this._onError(NLError.Basic.M3U8.MANIFEST_LOAD_ERROR, e.response);
    }.bind(this));

    return async;
  }

  loadVtts(url, userdata) {
    let async = XHRUtil.createXHREx({
      url: url
    }, userdata).then(function (ab) {
      // parse vtts
      if (userdata && ab.userdata && ab.userdata.type === 'vtts') {
        let parser = new WebVTTParser();
        let result = parser.parse(ab.response, 'metadata');
        try {
          this._parseVttPlaylist(result, ab.userdata);
        } catch (e) {
          this._onError(NLError.Basic.M3U8.PARSE_VTT_FAILED, e);
        }
      }
    }.bind(this)).catch(function (e) {
      this._onError(NLError.Basic.M3U8.GET_VTT_FAILED, e.response);
    }.bind(this));

    return async;
  }

  setPlayHeadCB(playHeadTimeCb) {
    this._playHeadTimeCb = playHeadTimeCb;
  }

  setErrorCb(errorCb) {
    this._errorCb = errorCb;
  }

  setThumbnailCb(thumbnailCb) {
    this._thumbnailCb = thumbnailCb;
  }

  setThumbnailImageCb(thumbnailImageCb) {
    this._thumbnailImageCb = thumbnailImageCb;
  }

  setAdStitchCb(adCb) {
    this._adCb = adCb;
  }

  getAbsUrl(url) {
    let absUrl = url;
    if (!UrlUtil.isAbsoluteUrl(url) && this._baseUrl) {
      absUrl = this._baseUrl + url;
    }
    return absUrl;
  }

  getLevelUrl() {
    return this._levelUrl;
  }

  getHlsCC() {
    return this._ccInfos;
  }

  _onError(descript, error) {
    if (this._errorCb) {
      this._errorCb({ error: descript, code: error });
    }
  }

  _parseMasterPlaylist(parser) {
    if (parser.manifest && parser.manifest.mediaGroups && parser.manifest.mediaGroups['CLOSED-CAPTIONS'] && 
        parser.manifest.mediaGroups['CLOSED-CAPTIONS'].ccs) {
      let ccs = parser.manifest.mediaGroups['CLOSED-CAPTIONS'].ccs;
      let index = 1;
      if (ccs && Object.keys(ccs).length > 0) {
        this._ccInfos = {};
        for (let i in ccs) {
          this._ccInfos['cc' + index.toString()] = {
            name: i,
            lang: ccs[i].language
          };
        }
      }
    }
    for (let i = 0; i < parser.manifest.playlists.length; i++) {
      let sbrUrl = parser.manifest.playlists[i].uri;
      if (!UrlUtil.isAbsoluteUrl(sbrUrl)) {
        sbrUrl = this._baseUrl + sbrUrl;
      }
      if (this._levelUrl == null) {
        this._levelUrl = sbrUrl;
        this._levelIndex = i;
      }
      this._levelRedundantUrls.push(sbrUrl);
    }
    this.loadPlaylist(this._levelUrl, { type: 'level', index: 0 });

    let thumbnails = parser.getThumbnailsStreams();
    if (thumbnails.length > 0 && this._thumbnailCb) {
      let upStreams = [];
      for (let i = 0; i < thumbnails.length; i++) {
        let thumbnailUri = thumbnails[0].uri;
        if (!UrlUtil.isAbsoluteUrl(thumbnailUri)) {
          thumbnailUri = this._baseUrl + thumbnailUri;
        }
        // this.loadPlaylist(thumbnailUri, {type: 'thumbnail', index: i});
        let upStream = {
          id: i,
          width: thumbnails[0].width,
          height: thumbnails[0].height,
          uri: thumbnailUri
        };
        upStreams.push(upStream);
      }
      this._thumbnailCb(upStreams);
    }
  }

  _parseLevelPlaylist(parser, index) {
    if (this._playHeadTimeCb) {
      this._playHeadTimeCb(parser.manifest, index);
    }
    if (this._adCb) {
      this._adCb(parser.adsInfos);
    }
  }

  _parseThumbnailPlaylist(parser, userdata) {
    if (this._thumbnailCb) {
      this._thumbnailCb([], parser, userdata);
    }
  }

  _parseVttPlaylist(result, userdata) {
    let index = userdata.index;
    let time = userdata.time;
    let baseTime = userdata.baseTime;
    let requestTime = userdata.requestTime;

    let thumbnails = {
      'images': [],
      'baseTime': baseTime,
      'requestTime': time,
      'streamId': index
    };
    if (this._thumbnailImageCb) {
      if (result.cues && result.cues.length > 0) {
        for (let i = 0; i < result.cues.length; i++) {
          let parts = result.cues[i].text.split('#xywh=');
          if (parts != null && parts.length === 2) {
            let positions = parts[1].split(',');
            if (positions == null || positions.length !== 4) {
              continue;
            }
            let image = {
              'url': this.getAbsUrl(parts[0]),
              'startTime': baseTime + result.cues[i].startTime,
              'endTime': baseTime + result.cues[i].endTime,
              'topX': positions[0],
              'topY': positions[1],
              'width': positions[2],
              'height': positions[3]
            };
            thumbnails.images.push(image);
          }
        }
        this._thumbnailImageCb(thumbnails, requestTime);
      }
    }
  }
}

import * as StringUtil from '../Utils/String'

export class ID3 {
    constructor(data) {
        this._dict = {};
        this._hasTimeStamp = false;
        let offset = 0;
        let byte1;
        let byte2;
        let byte3;
        let byte4;
        let tagSize;
        let endPos;
        let header;
        let len;
        do {
            header = this._readUTF(data, offset, 3);
            offset += 3;
            // first check for ID3 header
            if (header === 'ID3') {
                // skip 24 bits
                offset += 3;
                // retrieve tag(s) length
                byte1 = data[offset++] & 0x7f;
                byte2 = data[offset++] & 0x7f;
                byte3 = data[offset++] & 0x7f;
                byte4 = data[offset++] & 0x7f;
                tagSize = (byte1 << 21) + (byte2 << 14) + (byte3 << 7) + byte4;
                endPos = offset + tagSize;

                // read ID3 tags
                this._parseID3Frames(data, offset, endPos);
                offset = endPos;
            } else if (header === '3DI') {
                // http://id3.org/id3v2.4.0-structure chapter 3.4.   ID3v2 footer
                offset += 7;
                offset = endPos;
            } else {
                offset -= 3;
                len = offset;
                if (len) {
                    if (!this._hasTimeStamp) {
                        // ID3 tag found, but no timestamp
                    }
                    this._length = len;
                    this._payload = data.subarray(0, len);
                }
                return;
            }
        } while (offset < endPos);
    }

    _readUTF(data, start, len) {
        let result = '';
        let offset = start;
        let end = start + len;
        do {
            result += String.fromCharCode(data[offset++]);
        } while (offset < end);
        return result;
    }

    _getFrameSize(data, offset) {
        let size = 0;
        size = ((data[offset] & 0x7f) << 21);
        size |= ((data[offset + 1] & 0x7f) << 14);
        size |= ((data[offset + 2] & 0x7f) << 7);
        size |= (data[offset + 3] & 0x7f);
        return size;
    }

    _decodeTextFrame(frame, offset, type, size) {
        if (size < 2) {
            return null;
        }

        if (type === 'TXXX') {
            /*
            Format:
            [0]   = {Text Encoding}
            [1-?] = {Description}\0{Value}
            */
            let index = 1;
            let description = StringUtil.utf8ArrayToStr(frame.subarray(offset + index));

            index += description.length + 1;
            let value = StringUtil.utf8ArrayToStr(frame.subarray(offset + index));

            return { info: description, data: value };
        } else {
            /*
            Format:
            [0]   = {Text Encoding}
            [1-?] = {Value}
            */
            let text = StringUtil.utf8ArrayToStr(frame.subarray(offset + 1));
            return { info: '', data: text };
        }
    }

    _decodeURLFrame(frame, offset, type, size) {
        if (type === 'WXXX') {
            /*
            Format:
            [0]   = {Text Encoding}
            [1-?] = {Description}\0{URL}
            */
            if (size < 2) {
                return null;
            }

            let index = 1;
            let description = StringUtil.utf8ArrayToStr(frame.subarray(offset + index));

            index += description.length + 1;
            let value = StringUtil.utf8ArrayToStr(frame.subarray(offset + index));

            return { info: description, data: value };
        } else {
            /*
            Format:
            [0-?] = {URL}
            */
            let url = StringUtil.utf8ArrayToStr(frame.data);
            return { info: '', data: url };
        }
    }

    _parseID3Frames(data, offset, endPos) {
        let tagId;
        // let tagStart;
        // let tagLen;
        // let tagFlags;
        let timestamp;
        if (offset + 8 <= endPos) {
            tagId = this._readUTF(data, offset, 4);
            offset += 4;
            this._dict['key'] = tagId;
            switch (tagId) {
                case 'GEOB':
                    // skip tag flags and len;
                    offset += 7;
                    if (this._readUTF(data, offset, 15) === 'application/xml') {
                        offset += 15;

                        offset += 1;
                        if (this._readUTF(data, offset, 18) === 'substitution.plist') {
                            offset += 18;

                            offset += 2;

                            let xmlStr = this._readUTF(data, offset, endPos - offset);
                            offset += endPos - offset;
                            let parser = new DOMParser();
                            let xmlDoc = parser.parseFromString(xmlStr, 'application/xml');
                            if (xmlDoc.getElementsByTagName('dict').length > 1) {
                                // assert(xmlDoc.getElementsByTagName('dict')[0].childNodes[0].nodeValue === 'ad-list');
                                let nodes = xmlDoc.getElementsByTagName('dict')[1].childNodes;
                                let nodeValues = [];
                                for (let i = 0; i < nodes.length; i++) {
                                    if (nodes[i].nodeType === 1 && nodes[i].nodeName) { // element type and had the node name
                                        nodeValues.push({ node: nodes[i].nodeName, value: nodes[i].textContent.trim() });
                                    }
                                }
                                // assert(nodeValues.length % 2 === 0);
                                for (let j = 0; j < nodeValues.length; j++) {
                                    let keyIndex = j++;
                                    // assert(nodeValues[keyIndex].node === 'keyIndex');
                                    this._dict[nodeValues[keyIndex].value] = nodeValues[j].value;
                                }
                            }
                        }
                    }
                    break;
                case 'PRIV':
                    // tagLen = data[offset++] << 24 +
                    //     data[offset++] << 16 +
                    //     data[offset++] << 8 +
                    //     data[offset++];

                    // tagFlags = data[offset++] << 8 +
                    //     data[offset++];

                    // tagStart = offset;

                    // owner should be 'com.apple.streaming.transportStreamTimestamp'
                    if (this._readUTF(data, offset, 44) === 'com.apple.streaming.transportStreamTimestamp') {
                        offset += 44;
                        // smelling even better ! we found the right descriptor
                        // skip null character (string end) + 3 first bytes
                        offset += 4;

                        // timestamp is 33 bit expressed as a big-endian eight-octet number, with the upper 31 bits set to zero.
                        let pts33Bit = data[offset++] & 0x1;
                        this._hasTimeStamp = true;

                        timestamp = ((data[offset++] << 23) +
                            (data[offset++] << 15) +
                            (data[offset++] << 7) +
                            data[offset++]) / 45;

                        if (pts33Bit) {
                            timestamp += 0x100000000;
                        }
                        timestamp = Math.round(timestamp);
                        this._dict['info'] = timestamp;
                        this._dict['type'] = 'transportStreamTimestamp';
                    } else {
                        this._dict['info'] = this._readUTF(data, offset, endPos - offset - 2);
                        this._dict['type'] = 'data';
                    }
                    break;
                default:
                    {
                        let size = this._getFrameSize(data, offset);
                        let result = null;
                        if (tagId[0] === 'T' && endPos > 20) {
                            result = this._decodeTextFrame(data, 20, tagId, size);
                        } else if (tagId[0] === 'W' && endPos > 20) {
                            result = this._decodeURLFrame(data, 20, tagId, size);
                        } else {
                            this._dict['info'] = 'Unknown';
                        }
                        if (result !== null && this._freeWheelIAB(tagId, result) === false) {
                            this._dict['info'] = result.info;
                            this._dict['data'] = result.data;
                        }
                    }
                    break;
            }
        }
    }

    _freeWheelIAB(tagId, tit) {
        let freeWheelAd = false;
        if (tagId === 'TIT2' || tagId === 'TIT3') {
            let freeWheelReg = new RegExp('.*(fw_(.*)_(firstQuartile|midPoint|thirdQuartile|complete|defaultImpression|default))');
            let result = freeWheelReg.exec(tit.data);
            if (result) {
                this._dict['id'] = result[2];
                this._dict['adType'] = result[3];
                this._dict['value'] = result[0]; // keep same with ios, for example, keep the '_' in _fw_9529098_318_defaultImpression.
                freeWheelAd = true;
            }
        }
        return freeWheelAd;
    }

    dict() {
        return this._dict;
    }
}

/** 
 * @class NLError
 */
export class NLError {
  constructor() {
    this.code = 0;
    this.level = NLError.SeverityLevel.All;
    this.message = '';
    this.nativeMessage = '';
    this.url = '';
    this.httpCode = 0;
    this.nativeCode = 0;
  }

  static create(code, level, url, message, nativeCode) {
    let error = new NLError();
    error.code = code;
    error.level = level;
    error.url = url;
    error.nativeMessage = message;
    error.nativeCode = nativeCode;
    if (typeof (code) === 'number') {
      let tempStatus = code % 1000;
      if (tempStatus >= 400 && tempStatus < 600) {
        error.httpCode = tempStatus;
      }
    }
    let ProbeErrorItem = NLError.ErrorCodeString().filter(function (item) {
      if (code >= item.value && code < item.value + 1000) {
        return true;
      }
    }).pop();
    if (ProbeErrorItem && ProbeErrorItem.name) {
      error.message = ProbeErrorItem.name.replace(/_/g, ' ');
    }
    return error;
  }

  isValid() {
    return this.code >= NLError.Code.Device_Related_Error;
  }

  clone(code, level, url, message) {
    this.code = code;
    this.level = level;
    this.url = url;
    this.nativeMessage = message;
    if (typeof (code) === 'number') {
      let tempStatus = code % 1000;
      if (tempStatus >= 400 && tempStatus < 600) {
        this.httpCode = tempStatus;
      }
    }
    let ProbeErrorItem = NLError.ErrorCodeString().filter(function (item) {
      if (code >= item.value && code < item.value + 1000) {
        return true;
      }
    }).pop();
    if (ProbeErrorItem && ProbeErrorItem.name) {
      this.message = ProbeErrorItem.name.replace(/_/g, ' ');
    }
  }

  toJSON() {
    return {
      'errorCode': this.code,
      'errorLevel': this.level,
      'errorUrl': this.url,
      'errorNativeMessage': this.nativeMessage,
      'errorMsg': this.message,
      'errorHttpCode': this.httpCode,
      'errorNativeCode': this.nativeCode
    }
  }

  compare(other) {
    return (this.errorLevel === other.errorLevel) && (this.code === other.code) && (this.url === other.url) && (this.nativeMessage === other.nativeMessage);
  }
}

/**
 * Severity level of Error
 * @readonly
 * @enum {number}
 * @static
 * @type NLError.SeverityLevel
 */
NLError.SeverityLevel = {
  None: 0,
  Fatal: 1,
  Error: 2,
  Warning: 3,
  Info: 4,
  All: 15
}

/**
 * Error codes
 * @readonly
 * @enum {number}
 * @static
 * @type NLError.Code
 */
NLError.Code = {
  Device_Related_Error: 10000,
  Incompatible_Codecs_Error: 10005,
  DRM_Not_Supported: 10031,
  DRM_Init_Failed: 10032,
  // Playready_Not_Supported: 10031,
  // Widewine_Not_Supported: 10032,
  // FairPlay_Not_Supported: 10033,
  Platform_Not_Supported: 10040,
  // MediaSource_Error: 10050,
  // MediaElement_Error: 10060,

  Manifest_Related_Error: 20000,
  Manifest_Not_For_LTE: 20001,
  Get_Primary_Manifest_Failed: 21000,
  Get_Single_Bitrate_Manifest_Failed: 22000,

  KeyOrLicense_Related_Error: 30000,
  Get_Key_License_Failed: 31000,
  DRM_Module_Error: 32000,
  DRM_Module_Error_License_Expired: 32001,

  Chunk_Related_Error: 40000,
  Get_Chunk_Failed: 41000,

  Generic_Error: 50000,
  Start_Playback_Timeout: 51000,
  Seek_Timeout: 52000,
  Playback_Generic_Error: 53000
}

NLError.ErrorCodeString = function () {
  let ret = [];
  for (let name in NLError.Code) {
    if (!NLError.Code.hasOwnProperty(name)) {
      continue;
    } else {
      ret.push({ 'name': name, 'value': NLError.Code[name] });
    }
  }
  return ret;
}

/**
* Detailed network erro code
* @readonly
* @enum {number}
* @static
* @type NLError.NetworkDetailCode
*/
NLError.NetworkDetailCode = {
  Connect_Failed: 1,
  Timeout: 2,
  Security_Error: 51,
  Cookie_Reject_Error: 403,
  Http_Error: 404,
  Manifest_Not_Changed: 600,
  Content_Error: 800
}

NLError.Basic = {
