/**
 * A work-alike for EventTarget.  Only DOM elements may be true EventTargets,
 * but this can be used as a base class to provide event dispatch to non-DOM
 * classes.  Only FakeEvents should be dispatched.
 *
 * @class FakeEventTarget
 */
export class FakeEventTarget {
  constructor() {
    // @private {!shaka.util.MultiMap.<shaka.util.FakeEventTarget.ListenerType>}
    this.listeners_ = new MultiMap();
  }

  /**
   * Add an event listener to this object.
   *
   * @param {string} type The event type to listen for.
   * @param {ListenerType} listener The callback or
   *   listener object to invoke.
   * @param {boolean=} optCapturing Ignored.  do not have
   *   parents, so events neither capture nor bubble.
   * @override
   */
  addEventListener(type, listener, optCapturing) {
    this.listeners_.push(type, listener);
  }

  /**
   * Remove an event listener from this object.
   *
   * @param {string} type The event type for which you wish to remove a listener.
   * @param {ListenerType} listener The callback or
   *   listener object to remove.
   * @param {boolean=} optCapturing Ignored.  FakeEventTargets do not have
   *   parents, so events neither capture nor bubble.
   * @override
   */
  removeEventListener(type, listener, optCapturing) {
    this.listeners_.remove(type, listener);
  }

  destroy() {
    this.listeners_.clear();
  }

  /**
   * Dispatch an event from this object.
   *
   * @param {!Event} event The event to be dispatched from this object.
   * @return {boolean} True if the default action was prevented.
   * @override
   */
  dispatchEvent(event) {
    let list = this.listeners_.get(event.type) || [];

    for (let i = 0; i < list.length; ++i) {
      // Do this every time, since events can be re-dispatched from handlers.
      event.target = this;
      event.currentTarget = this;

      let listener = list[i];
      try {
        if (listener.handleEvent) {
          listener.handleEvent(event);
        } else {
          listener.call(this, event);
        }
      } catch (exception) {
        // Exceptions during event handlers should not affect the caller,
        // but should appear on the console as uncaught, according to MDN:
        // http://goo.gl/N6Ff27
        // console.log('Uncaught exception in event handler', exception);
      }

      if (event.stopped) {
        break;
      }
    }

    return event.defaultPrevented;
  }
}

/* eslint no-console: "off" */
let instance = null;

/**
 * class LogUtil
 */
export class LogUtil {
    constructor() {
        if (!instance) {
            instance = this;
        }
        this._level = LogUtil.LogLevel.INFO;
        return instance;
    }
    print(level, from, message) {
        if (level < this._level) {
            return;
        }
        let date = new Date().toTimeString().replace(/.*(\d{2}:\d{2}:\d{2}).*/, '$1');
        let text = '[' + date + '] ' + from + ': ' + message;
        switch (level) {
            case LogUtil.LogLevel.TRACE:
                text = '[TRACE] ' + text;
                console.trace(text);
                break;
            case LogUtil.LogLevel.DEBUG:
                text = '[DEBUG] ' + text;
                console.debug(text);
                break;
            case LogUtil.LogLevel.INFO:
                text = '[INFO] ' + text;
                console.info(text);
                break;
            case LogUtil.LogLevel.WARNING:
                text = '[WARNING] ' + text;
                console.warn(text);
                break;
            case LogUtil.LogLevel.ERROR:
                text = '[ERROR] ' + text;
                console.error(text);
                break;
            case LogUtil.LogLevel.HIGHLIGHTED:
                text = '[HIGHLIGHTED] ' + text;
                console.log(text);
                break;                
        }
    }

    setLogLevel(level) {
        this._level = level;
    }
}

/**
 * Log level
 * @readonly
 * @enum {number}
 * @static
 * @type LogUtil.LogLevel
 */
LogUtil.LogLevel = {
    TRACE: 0,
    DEBUG: 1,
    INFO: 2,
    WARNING: 3,
    ERROR: 4,
    HIGHLIGHTED: 99
};

import { Settings } from '../config';
import { LogUtil } from './Log';

export class Logger {
  constructor(logger) {
    this._logger = logger;
    this._logEnabled = true;
    this._logHighlighted = false;

    if (Settings.LOG_HIGHLIGHTED && typeof Settings.LOG_HIGHLIGHTED[this.constructor.name] === 'boolean') {
      this.enableHighlight(Settings.LOG_HIGHLIGHTED[this.constructor.name]);
    }
  }

  setLogLevel(level) {
    this._logger.setLogLevel(level);
  }

  enableLogger(enable) {
    this._logEnabled = !!enable;
  }

  print(level, from, message) {
    if (this._logEnabled) {
      this._logger.print(this._logHighlighted ? LogUtil.LogLevel.HIGHLIGHTED : level, from, message);
    }
  }

  enableHighlight(enable) {
    this._logHighlighted = !!enable;
    this._logEnabled = this._logHighlighted;
  }
}

