export class MultiMap {
  /**
   * A simple multimap template.
   * @constructor
   * @struct
   * @template T
   */
  constructor() {
    /** @private {!Object.<string, !Array.<T>>} */
    this.map_ = {};
  }

  /**
   * Add a key, value pair to the map.
   * @param {string} key
   * @param {T} value
   */
  push(key, value) {
    if (this.map_.hasOwnProperty(key)) {
      this.map_[key].push(value);
    } else {
      this.map_[key] = [value];
    }
  }

  /**
   * Set an array of values for the key, overwriting any previous data.
   * @param {string} key
   * @param {!Array.<T>} values
   */
  set(key, values) {
    this.map_[key] = values;
  }

  /**
   * Check for a key.
   * @param {string} key
   * @return {boolean} true if the key exists.
   */
  has(key) {
    return this.map_.hasOwnProperty(key);
  }

  /**
   * Get a list of values by key.
   * @param {string} key
   * @return {Array.<T>} or null if no such key exists.
   */
  get(key) {
    let list = this.map_[key];
    // slice() clones the list so that it and the map can each be modified
    // without affecting the other.
    return list ? list.slice() : null;
  }

  /**
   * Get a list of all values.
   * @return {!Array.<T>}
   */
  getAll() {
    let list = [];
    for (let key in this.map_) {
      list.push.apply(list, this.map_[key]);
    }
    return list;
  }

  /**
   * Remove a specific value, if it exists.
   * @param {string} key
   * @param {T} value
   */
  remove(key, value) {
    let list = this.map_[key];
    if (!list) { return; }
    for (let i = 0; i < list.length; ++i) {
      if (list[i] === value) {
        list.splice(i, 1);
        --i;
      }
    }
  }

  /**
   * Get all keys from the multimap.
   * @return {!Array.<string>}
   */
  keys() {
    let result = [];
    for (let key in this.map_) {
      result.push(key);
    }
    return result;
  }

  /**
   * Clear all keys and values from the multimap.
   */
  clear() {
    this.map_ = {};
  }
}

export function mergeObject(obj1, obj2) {
  let obj3 = {};
  for (let attrname in obj1) { obj3[attrname] = obj1[attrname]; }
  for (let attrname in obj2) { if (obj3[attrname] == null) obj3[attrname] = obj2[attrname]; }
  return obj3;
}

// Speed up calls to hasOwnProperty
let hasOwnProperty = Object.prototype.hasOwnProperty;
export function isEmpty(obj) {
  // null and undefined are 'empty'
  if (obj == null) { return true; }

  // Assume if it has a length property with a non-zero value
  // that that property is correct.
  if (obj.length > 0) { return false; }
  if (obj.length === 0) { return true; }

  // If it isn't an object at this point
  // it is empty, but it can't be anything *but* empty
  // Is it empty?  Depends on your application.
  if (typeof obj !== 'object') { return true; }

  // Otherwise, does it have any properties of its own?
  // Note that this doesn't handle
  // toString and valueOf enumeration bugs in IE < 9
  for (let key in obj) {
    if (hasOwnProperty.call(obj, key)) { return false; }
  }

  return true;
}

export function getKeyByValue(map, value) {
  for (let name in map) {
    if (map[name] === value) {
      return name;
    }
  }
  return '';
}

export function find(arr, propertyName, target) {
  if (!arr || arr.length <= 0) {
    return null;
  }

  let n = arr.length;
  for (let i = 0; i < n; i++) {
    if (arr[i][propertyName] === target) {
      return arr[i];
    }
  }

  return null;
}

import UAParser from '../../externals/ua-parser-js/ua-parser.min'

export function probePlatformSupport() {
  window.MediaSource = window.MediaSource || window.WebKitMediaSource;
  let mseSupport = !!(window.MediaSource &&
    typeof window.MediaSource.isTypeSupported === 'function' &&
    window.MediaSource.isTypeSupported('video/mp4; codecs="avc1.42E01E,mp4a.40.2"'));

  let browser = new UAParser().getBrowser();
  let browserLimitation = (browser.name !== 'IE') || (browser.name === 'IE' && browser.major >= 11); // IE 11+

  return mseSupport && browserLimitation;
}

export function probeWebRTCSupport() {
  let isWebRTCSupported = false;
  ['RTCPeerConnection', 'webkitRTCPeerConnection', 'mozRTCPeerConnection', 'RTCIceGatherer'].forEach(function (item) {
    if (isWebRTCSupported) {
      return;
    }

    if (item in window) {
      isWebRTCSupported = true;
    }
  });
  return isWebRTCSupported;
}

function isLocalStorageAvailable() {
  let test = 'test';
  try {
    localStorage.setItem(test, test);
    localStorage.removeItem(test);
    return true;
  } catch (e) {
    return false;
  }
}

export function generateUUID() {
  // http://stackoverflow.com/questions/105034/create-guid-uuid-in-javascript
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
    let r = Math.random() * 16 | 0;
    let v = c === 'x' ? r : r & 0x3 | 0x8;
    return v.toString(16);
  });
}

export function getDeviceId() {
  let id = generateUUID();

  // detect local storage available or not
  // if not available, use different id every time
  if (isLocalStorageAvailable()) {
    let oldId = localStorage.getItem('deviceId');
    if (!oldId) {
      localStorage.setItem('deviceId', id);
    } else {
      id = oldId;
    }
  }

  return id;
}

export function toDateString(epoch) {
    let date = new Date(epoch * 1000);
    return (date.getMonth() + 1) + '/' +
        date.getDate() + '/' +
        date.getFullYear() + ' ' +
        date.getHours() + ':' +
        date.getMinutes() + ':' +
        date.getSeconds()
}

export function formatSeconds(seconds) {
    let date = new Date(1970, 0, 1);
    date.setSeconds(seconds);
    return date.toTimeString().replace(/.*(\d{2}:\d{2}:\d{2}).*/, '$1');
}

export function formatBytes(bytes, decimals) {
    if (bytes === 0) { return '0 Byte'; }
    let k = 1024;
    let dm = decimals || 2
    let sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];
    let i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
}

export function utf8ArrayToStr(array) {
    let c;
    let char2;
    let char3;

    let out = '';
    let len = array.length;
    let i = 0;
    while (i < len) {
        c = array[i++];
        switch (c >> 4) {
            case 0: case 1: case 2: case 3: case 4: case 5: case 6: case 7:
                // 0xxxxxxx
                out += String.fromCharCode(c);
                break;
            case 12: case 13:
                // 110x xxxx   10xx xxxx
                char2 = array[i++];
                out += String.fromCharCode(((c & 0x1F) << 6) | (char2 & 0x3F));
                break;
            case 14:
                // 1110 xxxx  10xx xxxx  10xx xxxx
                char2 = array[i++];
                char3 = array[i++];
                out += String.fromCharCode(((c & 0x0F) << 12) |
                    ((char2 & 0x3F) << 6) |
                    ((char3 & 0x3F) << 0));
                break;
        }
    }

    return out;
}

export function hexStringToArray(str) {
    let result = [];
    while (str.length >= 2) {
        result.push(parseInt(str.substring(0, 2), 16));
        str = str.substring(2, str.length);
    }
    return result;
}

export function base64EncodeUint8Array(input) {
    let str;
    if (typeof input === 'string') {
        str = input;
    } else if (input instanceof ArrayBuffer) {
        str = String.fromCharCode.apply(null, new Uint8Array(input));
    } else if (input instanceof Uint8Array) {
        str = String.fromCharCode.apply(null, input);
    } else {
        return '';
    }

    return window.btoa(str);
}

export function base64DecodeUint8Array(input) {
    let raw = window.atob(input);
    let rawLength = raw.length;
    let array = new Uint8Array(new ArrayBuffer(rawLength));

    for (let i = 0; i < rawLength; i++) {
        array[i] = raw.charCodeAt(i);
    }

    return array;
}

export function stringToArray(string) {
    let buffer = new ArrayBuffer(string.length * 2); // 2 bytes for each char
    let array = new Uint16Array(buffer);
    for (let i = 0, strLen = string.length; i < strLen; i++) {
        array[i] = string.charCodeAt(i);
    }
    return array;
}

export function arrayToString(array) {
    let uint16array = new Uint16Array(array.buffer);
    return String.fromCharCode.apply(null, uint16array);
}

export function endsWith(str, search, thisLen) {
    if (thisLen == null || thisLen > str.length) {
        thisLen = str.length;
    }
    return str.substring(thisLen - search.length, thisLen) === search;
}

export function absTimeToRelTime(time, timeRanges, absStart) {
  if (time < absStart) {
    time = absStart;
  } else if (timeRanges.length > 0 && time > timeRanges[timeRanges.length - 1].end) {
    time = timeRanges[timeRanges.length - 1].end;
  }

  let calcTime = time;
  let offset = 0;
  for (let i = 0; i < timeRanges.length; i++) {
    if (i !== 0) {
      offset += (timeRanges[i].begin - timeRanges[i - 1].end);
    }
    if (time >= timeRanges[i].begin && time <= timeRanges[i].end) {
      calcTime = time - offset - absStart;
      break;
    } else {
      if (time < timeRanges[i].begin) {
        calcTime = timeRanges[i].begin - offset - absStart;
        break;
      }
      if (i !== 0) {
        if (time > timeRanges[i - 1].end && time < timeRanges[i].begin) {
          calcTime = timeRanges[i].begin - offset - absStart;
          break;
        }
        if (i === timeRanges.length - 1 && time > timeRanges[i].end) {
          calcTime = timeRanges[i].end - offset - absStart;
          break;
        }
      }
    }
  }
  return calcTime;
}

export function relTimeToAbsTime(time, timeRanges, absStart) {
  let calcTime = time;
  let offset = 0;
  for (let i = 0; i < timeRanges.length; i++) {
    if (i !== 0) {
      offset += (timeRanges[i].begin - timeRanges[i - 1].end);
    }
    let relBegin = timeRanges[i].begin - offset - absStart;
    let relEnd = timeRanges[i].end - offset - absStart;
    if (time >= relBegin && time <= relEnd) {
      calcTime = time + offset + absStart;
      break;
    } else {
      if (time < relBegin) {
        calcTime = relBegin + offset + absStart;
        break;
      }
      if (i !== 0) {
        let lastOffset = offset - (timeRanges[i].begin - timeRanges[i - 1].end);
        let lastRelEnd = timeRanges[i - 1].end - lastOffset - absStart;
        if (time > lastRelEnd && time < relBegin) {
          calcTime = relBegin + offset + absStart;
          break;
        }
        if (i === timeRanges.length - 1 && time > relEnd) {
          calcTime = relEnd + offset + absStart;
          break;
        }
      }
    }
  }
  return calcTime;
}

export function getAbsSafeTime(absTime, timeRanges, absEndLimit) {
  let safeAbsTime = absTime;
  if (timeRanges && timeRanges.length > 0) {
    if (absTime <= timeRanges[0].begin) {
      safeAbsTime = timeRanges[0].begin;
      return safeAbsTime;
    }
    if (absTime >= timeRanges[timeRanges.length - 1].end) {
      safeAbsTime = timeRanges[timeRanges.length - 1].end;
      if (absEndLimit) {
        safeAbsTime = Math.min(absEndLimit, safeAbsTime);
      }
      return safeAbsTime;
    }
    for (let i = 0; i < timeRanges.length; i++) {
      let lastEnd = timeRanges[i].begin;
      if (i !== 0) {
        lastEnd = timeRanges[i - 1].end;
      }
      if (absTime >= lastEnd && absTime <= timeRanges[i].begin) {
        safeAbsTime = timeRanges[i].begin;
        break;
      }
      if (absTime > timeRanges[i].begin && absTime <= timeRanges[i].end) {
        break;
      }
    }
  }

  return safeAbsTime;
}

export function vacancyTime(absTime, timeRanges) {
  let vacancy = true;

  for (let i = 0; i < timeRanges.length; i++) {
    if (absTime >= timeRanges[i].begin && absTime <= timeRanges[i].end) {
      vacancy = false;
      break;
    }
  }

  return vacancy;
}

function _findTimeRanges(timeRanges, time) {
    let range = null;
    for (let i = timeRanges.length - 1; i >= 0; i--) {
        if ((timeRanges[i].end === null && time >= timeRanges[i].begin) ||
            (timeRanges[i].end !== null && time >= timeRanges[i].begin && time <= timeRanges[i].end)) {
            range = timeRanges[i];
            break;
        }
    }
    return range;
}

export function mergeProgrameTime(timeline, dateRangesInM3u, targetDurationInM3u, playlistDurationInM3u) {
  let dateRanges = dateRangesInM3u;
  let timeRanges = timeline.timeRanges;

  timeline.targetDuration = targetDurationInM3u || 0;

  if (dateRanges.length > 0) {
    for (let i = 0; i < dateRanges.length; i++) {
      if (i === 0 && !timeline.init) {
        timeline.init = true;
        timeline.absStart = dateRanges[i].begin;
      }
      let range = _findTimeRanges(timeline.timeRanges, dateRanges[i].begin);
      if (range === null) {
        range = {
          begin: dateRanges[i].begin,
          end: dateRanges[i].end
        };
        timeRanges.push(range);
      } else {
        if (range.end < dateRanges[i].end) {
            range.end = dateRanges[i].end;
        }
      }
      if (i === 0) {
        timeline.curAbsStart = dateRanges[i].begin;
      }
      if (i === dateRanges.length - 1) {
        timeline.curAbsEnd = dateRanges[dateRanges.length - 1].end;
      }
    }
  } else {
    timeline.curAbsEnd = playlistDurationInM3u;
  }
}

import UrlValidator from '../../externals/valid-url/index'
import { AdaptivePlayer } from '../AdaptivePlayer';

export function validateURL(url) {
  return UrlValidator.isWebUri(url);
}

export function isAbsoluteUrl(url) {
  if (typeof url !== 'string') {
    return false;
  }
  return UrlValidator.isWebUri(url);
}

export function getPath(url) {
  let tmp = url.split('?')[0];
  return tmp.substr(0, tmp.lastIndexOf('/') + 1);
}

export function getExtension(url) {
  if (!url) { return ''; }

  let parts = url.split('?')[0].split('.');
  // Handle files with no extensions and hidden files with no extension
  if (parts.length === 1 || (parts[0] === '' && parts.length === 2)) {
    return '';
  }
  return parts.pop().toLowerCase();
}

export function detectPlayerType(url, protocol, isDRM) {
  if (url == null || url === '' || !validateURL(url)) {
    return AdaptivePlayer.PlayerType.UNKNOWN;
  }

  if (typeof protocol !== 'undefined') {
    switch (protocol) {
      case AdaptivePlayer.Protocol.HTTP:
        return AdaptivePlayer.PlayerType.NATIVE;
      case AdaptivePlayer.Protocol.WEBRTC:
        return AdaptivePlayer.PlayerType.WEBRTC;
      case AdaptivePlayer.Protocol.DASH:
        return AdaptivePlayer.PlayerType.SHAKA;
      case AdaptivePlayer.Protocol.HLS:
        return (AdaptivePlayer.browserType === 'Safari' || AdaptivePlayer.browserType === 'Mobile Safari' /* || browserType === 'Edge' */)
          ? AdaptivePlayer.PlayerType.NATIVE : AdaptivePlayer.PlayerType.HLSJS;
      default:
        return AdaptivePlayer.PlayerType.UNKNOWN;
    }
  } else {
    if (url.indexOf('ws://') !== -1 || url.indexOf('wss://') !== -1) { // webrtc streams
      return AdaptivePlayer.PlayerType.WEBRTC;
    }
    let ext = getExtension(url);
    if (ext === 'm3u8' || ext === 'm3u') {
      // Safari, use hls.js for non-drm contents
      // Or use native player for DRM contents
      // Mobile Safari uses native player (DRM not supported)
      // Other browser use hls.js
      if ((!!isDRM && AdaptivePlayer.browserType === 'Safari') || AdaptivePlayer.browserType === 'Mobile Safari' /* || browserType === 'Edge' */) {
        return AdaptivePlayer.PlayerType.NATIVE;
      }
      return AdaptivePlayer.PlayerType.HLSJS;
    } else if (ext === 'mpd') {
      return AdaptivePlayer.PlayerType.SHAKA;
    } else if (ext === 'mp4' || ext === 'webm' || ext === 'ogg') {
      return AdaptivePlayer.PlayerType.NATIVE;
    } else {
      return AdaptivePlayer.PlayerType.UNKNOWN;
    }
  }
}

let extensionToCanPlayType = function (ext) {
    if (ext === 'ogg') { return 'video/ogg'; }
    if (ext === 'mp4') { return 'video/mp4'; }
    if (ext === 'webm') { return 'video/webm'; }
    if (ext === 'm3u8') { return 'application/vnd.apple.mpegurl'; }
    if (ext === 'mpd') { return 'application/dash+xml'; }

    return '';
};

let canPlayNative = function (videoElement, ext) {
    let type = extensionToCanPlayType(ext);
    let canPlay = videoElement.canPlayType(type);

    return canPlay === 'maybe' || canPlay === 'probably';
};

let getHashNameOfTrack = function(track) {
    let hashName = '';
    hashName += (track.kind !== '') ? (track.kind + '_') : '';
    hashName += (track.language !== '') ? (track.language + '_') : '';
    hashName += (track.label !== '') ? track.label : '';
    return hashName;
};

export {
    extensionToCanPlayType,
    canPlayNative,
    getHashNameOfTrack
}

/* eslint prefer-promise-reject-errors: "off" */

export function createXHR(options) {
    let url = '';
    if (typeof options === 'string') {
        url = options;
        options = {};
    }

    options.url = options.url || url;
    options.responseType = options.responseType || '';
    options.method = options.method || 'GET';
    options.params = options.params || null;

    let xhr = null;
    let promise = new Promise(function (resolve, reject) {
        function onLoaded(event) {
            if (xhr.status >= 200 && xhr.status <= 299) {
                resolve(xhr.response);
            } else {
                reject(xhr.status);
            }
        }

        function onError() {
            reject(xhr.status);
        }

        let responseType = options.responseType;
        let url = options.url;
        let method = options.method;

        xhr = new XMLHttpRequest();
        xhr.responseType = responseType;
        xhr.addEventListener('load', onLoaded, false);
        xhr.addEventListener('error', onError, false);

        xhr.open(method, url, true);

        if (options.headers) {
            for (let key in options.headers) {
                xhr.setRequestHeader(key, options.headers[key]);
            }
        }
    });

    promise.getXHR = function () {
        return xhr;
    };

    promise.abort = function () {
        xhr.abort();
    };

    xhr.send(options.params);
    return promise;
}

function rand(length, current) {
    current = current || '';
    return length ? rand(--length, '0123456789ABCDEFGHIJKLMNOPQRSTUVWXTZabcdefghiklmnopqrstuvwxyz'.charAt(Math.floor(Math.random() * 60)) + current) : current;
}

export function sendWithIframe(url) {
    let id = 'url_iframe_' + rand(16);
    let iframe = document.getElementById(id);
    if (!iframe) {
        iframe = document.createElement('iframe');
        iframe.style.display = 'none';
        iframe.id = id;
        document.body.appendChild(iframe);
    }
    iframe.src = url;
    if (iframe.attachEvent) {
        iframe.attachEvent('onload', function () {
            // console.trace('Local iframe is now loaded.' + url + ' on ' + id);
            iframe.parentNode.removeChild(iframe);
        });
    } else {
        iframe.onload = function () {
            // console.trace('Local iframe is now loaded.' + url + ' on ' + id);
            iframe.parentNode.removeChild(iframe);
        };
    }
}

export function sendWithScript(url) {
    let id = 'url_script_' + rand(16);
    let script = document.getElementById(id);
    if (!script) {
        script = document.createElement('script');
        document.body.appendChild(script);
    }
    script.src = url;
    if (script.attachEvent) {
        script.attachEvent('onload', function() {
            // console.debug('Local script is now loaded.' + url + ' on ' + id);
            script.parentNode.removeChild(script);
        });
    } else {
        script.onload = function() {
            // console.debug('Local script is now loaded.' + url + ' on ' + id);
            script.parentNode.removeChild(script);
        };
    }
}

export function createXHREx(options, userdata) {
    let url = '';
    if (typeof options === 'string') {
        url = options;
        options = {};
    }

    options.url = options.url || url;
    options.responseType = options.responseType || '';
    options.method = options.method || 'GET';
    options.params = options.params || null;

    let xhr = null;
    let promise = new Promise(function(resolve, reject) {
        function onLoaded(event) {
            if (xhr.status >= 200 && xhr.status <= 299) {
                resolve({
                    response: xhr.response, 
                    userdata: userdata
                });
            } else {
                reject({
                    response: xhr.status,
                    userdata: userdata
                });
            }
        }

        function onError() {
            reject({
                response: xhr.status,
                userdata: userdata
            });
        }

        let responseType = options.responseType;
        let url = options.url;
        let method = options.method;

        xhr = new XMLHttpRequest();
        xhr.responseType = responseType;
        xhr.addEventListener('load', onLoaded, false);
        xhr.addEventListener('error', onError, false);

        xhr.open(method, url, true);

        if (options.headers) {
            for (let key in options.headers) {
                xhr.setRequestHeader(key, options.headers[key]);
            }
        }
    });

    promise.getXHR = function() {
        return xhr;
    };
    promise.abort = function() {
        xhr.abort();
    };

    xhr.send(options.params);
    return promise;
}
